DROP VIEW IF EXISTS `v_pm_state`;
CREATE ALGORITHM=UNDEFINED DEFINER=CURRENT_USER SQL SECURITY DEFINER VIEW `v_pm_state` AS select `t`.`pm_StateId` AS `pm_StateId`,`t`.`RecordCreated` AS `RecordCreated`,`t`.`RecordModified` AS `RecordModified`,`t`.`VPD` AS `VPD`,`t`.`OrderNum` AS `OrderNum`,`t`.`Caption` AS `Caption`,`t`.`Description` AS `Description`,`t`.`ObjectClass` AS `ObjectClass`,`t`.`IsTerminal` AS `IsTerminal`,`t`.`ReferenceName` AS `ReferenceName`,`t`.`QueueLength` AS `QueueLength`,`t`.`RecordVersion` AS `RecordVersion`,`t`.`RelatedColor` AS `RelatedColor`,`t`.`ArtifactsType` AS `ArtifactsType`,`t`.`ExcludeLeadTime` AS `ExcludeLeadTime`,`t`.`SkipEmailNotification` AS `SkipEmailNotification`,`t`.`IsNotChange` AS `IsNotChange`,(case `t`.`IsTerminal` when 'N' then 1 when 'I' then 2 else 3 end) AS `IsTerminalNum` from `pm_state` `t`;
